element.calculation:
(function calculatedFieldValue(current) {
	var display;
	if (current.sys_id.nil()) {
		display = "[new appropriation type]";
	}
	else {
		display = current.appropriation_type_desc.toString() + ' [' + current.appropriation_type.toString() + ']'; 
	}
	return display;
})(current);
