element.calculation:
(function calculatedFieldValue(current) {
	var display;
	if (current.sys_id.nil()) {
		display = "[new budget item]";
	}
	else {
		display = current.expense_item_ref.activity_code.toString() + ' [' + current.expense_item_ref.budget_code_ref.budget_code.toString() + ']'; 
	}
	return display;

})(current);

element.reference_qual:
javascript:  
var expItems = new FMSBudgetUtils().getItemExpRefs(current.budget_ref);
var refQual = "disabled=false^organization_ref=" + current.budget_ref.project_ref.organization_ref + "^sys_idNOT IN" + expItems.join(","); 
refQual;
