element.calculation:
(function calculatedFieldValue(current) {
	var display;
	if (current.sys_id.nil()) {
		display = "[new contract relationship]";
	}
	else {
		display = current.contract_ref.contract_piid.toString() + ' : ' + current.contract_rel_ref.contract_piid.toString() + ' [' + current.relationship_type.toString() + ']'; 
	}
	return display;
})(current);

element.reference_qual:
javascript: 

if(current.relationship_type.toString() == "Contract/Task Order"){

     if(current.contract_ref.contract_class.toString()=="Contract"){

           "contract_classIN" + current.relationship_type.split('/')[1].toString(); 
   }

      else if(current.contract_ref.contract_class.toString()=="Task Order"){

               "contract_classIN" + current.relationship_type.split('/')[0].toString(); 
    }
}

else{
 "contract_classIN" + current.relationship_type.split('/')[1].toString(); 
}
