element.calculation:
(function calculatedFieldValue(current) {
	var display;
	if (current.sys_id.nil()) {
		display = "[new project appropriation]";
	}
	else {
		display = current.appropriation_ref.appropriation.toString(); 
		if (current.appropriation_ref.appropriation_desc.toString().length > 0)
			display += ' [' + current.appropriation_ref.appropriation_desc.toString() + ']'; 
	}
	return display;

})(current);
