element.calculation:
(function calculatedFieldValue(current) {
	var display;
	if (current.sys_id.nil()) {
		display = "[new transfer type]";
	}
	else {
		display = current.transfer_type.toString() + '\t\t\t\t[' + current.transfer_type_code.toString() + ']'; 
	}
	return display;
})(current);
