sp_angular_provider.script:
function AGGridHelper(){
	return{
		setGrid:function(gridOptions, rowData) {

			/* Add grids to the DOM */
			var agGridDiv = document.querySelector('#AGgrid');
			new agGrid.Grid(agGridDiv, gridOptions);

			/* Associate back-end arrays to the front-end grids */
			gridOptions.api.setRowData(rowData);
			gridOptions.api.sizeColumnsToFit();

		}
	};
}
