sp_widget.client_script:
function(spModal) {
  /* widget controller */
  var c = this;

  c.addNewTran = function() {
    var wInput = {
      table: "x_g_irm_fms_fin_tran",
      sys_id: "-1",
      view: "portal"
    };

    spModal.open({
      title: 'New Transaction',
      size: 'lg',
      widget: 'widget-form',
      widgetInput: wInput
    });
  }

  c.addNewItem = function() {
    var wInput = {
      table: "x_g_irm_fms_fin_tran_item",
