    };

    spModal.open({
      title: 'New Item',
      size: 'lg',
      widget: 'widget-form',
      widgetInput: wInput
    });
  }

  c.addNewCon = function() {
    var wInput = {
      table: "x_g_irm_fms_contract",
      sys_id: "-1",
      view: "portal"
    };

    spModal.open({
      title: 'New Contract',
      size: 'lg',
      widget: 'widget-form',
      widgetInput: wInput
    });
  }

  c.addNewConParty = function() {
    var wInput = {
      table: "x_g_irm_fms_contract_party",
      sys_id: "-1"
    };

    spModal.open({
      title: 'New Party',
      size: 'lg',
      widget: 'widget-form',
      widgetInput: wInput
    });
  }

}
