sp_widget.client_script:
function($scope, AGGridFactory, $timeout, $uibModal) {
    /* widget controller */
    var c = this;
    c.targetOrg = c.data.budgetData['project_ref.organization_ref'];
    c.targetBudget = c.data.selectedBudget;
    c.userAuth = $rootScope.userAuth;

    var phaseManager = new AGGridFactory();

    c.addNew = function() {
        phaseManager.editRow("-1", "budget_ref=" + c.targetBudget);
    }

    c.canAdd = function() {
		if (c.data.budgetData.locked == true) return false;
        if (c.userAuth.roles.isSysAdmin || c.userAuth.roles.isOrgAdmin || c.userAuth.roles.isAnalyst) {
            if (c.userAuth.orgs.indexOf(c.targetOrg) >= 0)
                return true;
            else
                return false;
        } else
            return false;
    }

    c.canEdit = function(auth, data) {
		if (c.data.budgetData.locked == true) return false;
        if (auth.orgs.indexOf(c.targetOrg) >= 0 && (auth.roles.isOrgAdmin || auth.roles.isAnalyst))
            return true;
        else
            return false;
    }

    c.canDelete = function(auth, data) {
        return c.canEdit(auth, data);
    }

    c.exportExcel = function() {
        phaseManager.exportExcel();
    }

    c.openModal = function(itemID, itemDisplay) {
        c.server.get({
            mode: "getItemPhasing",
            budget_item_id: itemID
        }).then(function(resp) {
			c.data.itemPhasing = resp.data.itemPhasing;
			c.data.itemDisplay = itemDisplay;
			c.modalInstance = $uibModal.open({
				templateUrl: 'modalTemplate',
				scope: $scope
			});
		});
    }
    c.closeModal = function() {
        c.server.get({
            mode: "updateItemPhasing",
            items: c.data.itemPhasing
        }).then(function(resp) {
			c.modalInstance.close();
            $rootScope.$broadcast('BudgetDetailsGridChanged', {
                grid_type: 'phasing'
            });
		});
    }

    var phaseColumnDefs = [{
            headerName: 'Budget Code',
            field: 'sys_id',
            minWidth: 175,
            filter: 'agTextColumnFilter',
            cellRenderer: 'agGroupCellRenderer',
            showRowGroup: true,
            sort: 'asc',
            sortingOrder: 1,
            cellRendererParams: {
                suppressCount: true,
                checkbox: false,
                suppressDoubleClickExpand: true,
                suppressEnterExpand: true,
                innerRenderer: 'myGroupCellRenderer',
                banner: {
                    type: 'standard',
                    columns: [{
                        label: '',
//                        name: 'budget_item_ref.expense_item_ref.budget_code_ref.budget_code'
                        name: 'budget_code'
                    }]
                }
            }
        },
        {
            field: 'edit',
            headerName: 'Edit',
            maxWidth: 50,
            cellStyle: {
                textAlign: 'center'
            },
            suppressMenu: true,
            sortable: false,
            cellRenderer: 'editCell',
            cellRendererParams: function(params) {
                return {
                    "show": c.canEdit(c.userAuth, params.data)
                };
            }
        },
        {
//            field: 'budget_item_ref.expense_item_ref.budget_code_ref.budget_code',
            field: 'budget_code',
            headerName: 'Budget Code',
            filter: 'agTextColumnFilter',
            rowGroup: true,
            hide: true
        },
        {
            field: 'budget_item_ref_rawval',
            headerName: 'Expense Item',
            filter: 'agTextColumnFilter',
            rowGroup: true,
            enableRowGroup: true,
            cellRenderer: function(params) {
				var exp = params.node.childrenAfterGroup[0].data.expense_item_ref;
				var eDiv = document.createElement('div');
				eDiv.innerHTML = '';
				if (c.canAdd())
					eDiv.innerHTML += '<span class="row-button"><i class="fa fa-edit edit-pencil"></i></span>';
				eDiv.innerHTML += '<span title="' + exp + '"><b>' + exp + '</b></span>';
				var eButton = eDiv.querySelectorAll('.fa-edit')[0];
				if (eButton)
					eButton.addEventListener('click', function() {
						c.openModal(params.value, exp);
					});
				return eDiv;
			}
		},
        {
            field: 'fiscal_month',
            headerName: 'Phase Month',
            cellStyle: {
                textAlign: 'right'
            },
            pivot: true,
            enablePivot: true,
            pivotComparator: function(a, b) {
                var requiredOrder = ['Oct', 'Nov', 'Dec', 'Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep'];
                return requiredOrder.indexOf(a) - requiredOrder.indexOf(b);
            }
        },
        {
            field: 'phase_amt',
            headerName: 'Phased Amount',
            filter: 'agNumberColumnFilter',
            cellStyle: {
                textAlign: 'right'
            },
            type: 'currency',
            aggFunc: 'sum',
            headerComponentParams: {
                template: '<div class="ag-cell-label-container" role="presentation">' +
                    '<span ref="eText" class="ag-header-cell-text" role="columnheader"></span>' +
                    '</div>'
            }
        },
        {
            field: 'delete',
            headerName: 'Del.',
            maxWidth: 50,
            cellStyle: {
                textAlign: 'center'
            },
            suppressMenu: true,
            sortable: false,
            cellRenderer: 'deleteCell',
            cellRendererParams: function(params) {
                return {
                    "show": c.canDelete(c.userAuth, params.data)
                };
            }
        }
    ];

    var phaseProps = {
        pageController: c,
        tableName: c.data.tableName,
        dataArray: null,
        pageType: 'Item Phasing',
        linkedField: '',
        detailsPage: '',
    }

    c.colSecondary = function(colDef) {
		if (colDef.pivotKeys.length == 0) colDef.headerName = "Expense Total"
        colDef.headerName = colDef.headerName.toUpperCase();
    }

	phaseManager.setColumns(phaseColumnDefs);
    phaseManager.setGridOption('pivotRowTotals', 'before');
    phaseManager.setGridOption('processSecondaryColDef', c.colSecondary);
    phaseManager.setPageProperties(phaseProps);
    phaseManager.setRenderers('expense_item_ref'); //build renderers and set display field to show on delete attempt
    phaseManager.renderGrid('phaseGrid'); //render grid objects
    phaseManager.setColumnOption('setPivotMode', true);
    phaseManager.setGridData(c.data.tableData); //set grid data
    phaseManager.setColumnsVisible(['edit', 'delete'], (c.userAuth.roles.isSysAdmin || c.userAuth.roles.isOrgAdmin || c.userAuth.roles.isAnalyst));
    $timeout(function() {
        phaseManager.fitColumns();
    }, 250);

	$rootScope.$on('BudgetDetailsGridChanged', function(event, args) {
		if (!args.gridType == 'phasing') return;
        c.server.get({
            mode: "refresh",
            budget_id: c.targetBudget
        }).then(function(resp) {
            phaseManager.setGridData(resp.data.tableData); //set grid data
        });
    });

	$rootScope.$on('BudgetChanged', function(event, args) {
        c.server.get({
            mode: "refresh",
            budget_id: args.budget_id
        }).then(function(resp) {
			c.data.budgetData = resp.data.budgetData;
			c.targetOrg = resp.data.budgetData['project_ref.organization_ref'];
			c.targetBudget = resp.data.selectedBudget;
            phaseManager.setGridData(resp.data.tableData); //set grid data
        });
    });
}

sp_widget.script:
(function() {

	data.budgetOrg = "";
	data.selectedBudget = "";
    var FMSUtility = new x_g_irm_fms.FMSUtilities(); //helper utility for FMS queries

    var session = gs.getSession();
    if ($sp.getParameter("budget_id") && $sp.getParameter("budget_id").length > 0)
		data.selectedBudget = $sp.getParameter('budget_id');

    if (input) { //input holds the data coming back to the server
        if (input.mode == "getItemPhasing") {
			var phaseInclude = [ 'budget_item_ref.expense_item_ref.activity_code' ];
			data.itemPhasing = FMSUtility.getTableData(FMSConstants.TABLE_BUDGET_ITEM_PHASE, "budget_item_ref.sys_id=" + input.budget_item_id, true, phaseInclude); 
			if (data.itemPhasing.length == 0) {
				for (i = 1; i <= 12; i++) {
					var emptyRow = {};
					emptyRow.sys_id = "";
					emptyRow.budget_item_ref = input.budget_item_id;
					emptyRow.budget_item_ref_rawval = input.budget_item_id;
					emptyRow.fiscal_month = ['Oct', 'Nov', 'Dec', 'Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep'][i - 1];
					emptyRow.fiscal_month_rawval = i;
					emptyRow.phase_amt = 0;
					data.itemPhasing.push(emptyRow);
				}
			}
		}
        if (input.mode == "updateItemPhasing") {
		}
    }


	var budgetInclude = [ 'project_ref.organization_ref' ];
//	var phaseInclude = [ 'budget_item_ref.expense_item_ref.budget_code_ref.budget_code' ];
	data.budgetData = FMSUtility.getTableData(FMSConstants.TABLE_BUDGET, "sys_id=" + data.selectedBudget, true, budgetInclude)[0];
//    data.tableData = FMSUtility.getTableData(FMSConstants.TABLE_BUDGET_ITEM_PHASE, "budget_item_ref.budget_ref.sys_id=" + data.selectedBudget, true, phaseInclude); 
	data.tableData = [];
    var phase = new GlideRecord(FMSConstants.VIEW_BUDGET_PHASING);
    phase.addQuery('bud_sys_id', data.selectedBudget);
	phase.query();
	while (phase.next()) {
		var phaseRow = {};
		phaseRow.sys_id = phase.phz_sys_id.toString();
		phaseRow.budget_item_ref = phase.phz_budget_item_ref.getDisplayValue();
		phaseRow.budget_item_ref_rawval = phase.phz_budget_item_ref.toString();
		phaseRow.budget_code = phase.exp_budget_code_ref.getDisplayValue();
		phaseRow.expense_item_ref = phase.itm_expense_item_ref.getDisplayValue();
		phaseRow.expense_item_ref_rawval = phase.itm_expense_item_ref.toString();
		phaseRow.activity_code = phase.exp_activity_code.toString();
		phaseRow.fiscal_month = phase.phz_fiscal_month.getDisplayValue();
		phaseRow.fiscal_month_rawval = parseInt(phase.phz_fiscal_month.toString());
		phaseRow.phase_amt = parseFloat(phase.phz_phase_amt.toString());
		if (phase.phz_sys_id.nil()) {
			for (i = 1; i <= 12; i++) {
				var emptyRow = {};
				emptyRow.sys_id = phase.itm_sys_id.toString();
				emptyRow.budget_item_ref = phase.itm_sys_id.getDisplayValue();
				emptyRow.budget_item_ref_rawval = phase.itm_sys_id.toString();
				emptyRow.budget_code = phase.exp_budget_code_ref.getDisplayValue();
				emptyRow.expense_item_ref = phase.itm_expense_item_ref.getDisplayValue();
				emptyRow.expense_item_ref_rawval = phase.itm_expense_item_ref.toString();
				emptyRow.activity_code = phase.exp_activity_code.toString();
				emptyRow.fiscal_month = ['Oct', 'Nov', 'Dec', 'Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep'][i - 1];
				emptyRow.fiscal_month_rawval = i;
				emptyRow.phase_amt = 0;
				data.tableData.push(emptyRow);
			}
		}
		else
			data.tableData.push(phaseRow);
	}

    data.tableName = FMSConstants.TABLE_BUDGET_ITEM_PHASE;

})();
