sp_widget.client_script:
function ($rootScope) {
  /* widget controller */
  var c = this;

  $rootScope.$on('OrgChanged', function (event, args) {
    c.server.get({
      mode: "setOrg",
      org_id: args.org_id
    }).then(function (resp) {
      c.data.selOrg = resp.data.selOrg;
    });
  });

}

