sp_widget.client_script:
function ($timeout) {
	/* widget controller */
	var c = this;
	c.targetOrg = c.data.selectedOrg;
	c.userAuth = $rootScope.userAuth;
	c.selectedBudgetType = "Baseline";

	c.reportData = [];

	c.generateReport = function () {

		var reportObj = {
			mode: 'buildReport',
			fy: c.selectedFY
		};

		if (c.data.reportType == "budget_plans")
			reportObj.budget_type = c.selectedBudgetType;

		c.server.get(reportObj).then(function (reportResults) {
			c.reportData = reportResults.data.reportData;
			gridOptions.api.setRowData(c.reportData);
			gridOptions.api.sizeColumnsToFit();
		});
	}

	c.getReportTitle = function() {
		var title = "";
		switch (c.data.reportType) {
			case "allocation":
			  title = "Allocation Activity";
			  break;
			case "baseline":
			  title = "Baseline Activity";
			  break;
			case "budget_plans":
			  title = "Budget Plans";
		}
		title += " Report";
		return title
	}

	c.reportTitle = c.getReportTitle();

	c.toggleExpand = function (mode) {
		gridOptions.api.forEachNode(function (rowNode, index) {
			if (rowNode.group)
				rowNode.setExpanded(mode);
		});
	}

	function currencyCssFunc(params) {
		if (params.value !== null && params.value !== undefined && params.value < 0) {
			return {
				"color": "red",
				"font-weight": "bold",
				"text-align": "right"
			};
		} else {
			return {
				"text-align": "right"
			};
		}
	}

	function currencyFormatter(params) {
		if (params.value != 0 && (params.value === null || params.value === undefined || params.value == "")) {
			return null;
		} else if (isNaN(params.value)) {
			return '$' + parseFloat(0).toFixed(2).replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,");
		} else {
			if (parseFloat(params.value) >= 0) {
				return '$' + parseFloat(params.value).toFixed(2).replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,");
			} else {
				return currencyNegative(params.value);
			}
		}
	}

	function currencyNegative(tVal) {
		return '$(' + parseFloat(tVal).toFixed(2).replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,").replace("-", "") + ")";
	}

	function generateColumns() {
		var colArray = [];
		if (c.data.reportType == "allocation") {
			colArray.push({
				headerName: 'Appropriation',
				field: 'appropriation',
				rowGroup: true,
				rowGroupIndex: 1,
				enableRowGroup: true,
				enablePivot: true,
				filter: true
			});
			colArray.push({
				headerName: 'Appropriation Type',
				field: 'appropriation_type',
				enableRowGroup: true,
				enablePivot: true,
				filter: true
			});
			colArray.push({
				headerName: 'Allocation Group',
				field: 'allocation_group',
				enableRowGroup: true,
				filter: true
            });
		}

		if (c.data.reportType == "budget_plans") {
			colArray.push({
                headerName: 'Budget',
                field: 'budget_name',
				enableRowGroup: true,
				filter: true
			});
		}

		if (c.data.reportType == "baseline" || c.data.reportType == "budget_plans") {
            colArray.push({
                headerName: 'Budget Code',
                field: 'budget_code',
                enableRowGroup: true,
				rowGroup: true,
				rowGroupIndex: 0,
				filter: true
			});
			colArray.push({
                headerName: 'Activty Code',
                field: 'activity_code',
                enableRowGroup: true,
				rowGroup: true,
				rowGroupIndex: 1,
				filter: true
			});
		}

        colArray.push({
            headerName: 'Investment',
            field: 'investment',
            enableRowGroup: true,
			filter: true
        });
        colArray.push({
            headerName: 'Organization',
            field: 'org_name',
            enablePivot: true,
            enableRowGroup: true,
			filter: true
        });
        colArray.push({
            headerName: 'Project',
            field: 'project',
            pivot: false,
            enablePivot: true,
            enableRowGroup: true,
			filter: true
        });
        colArray.push({
            headerName: 'OMB Project',
            field: 'omb_project',
            pivot: false,
            enablePivot: true,
            enableRowGroup: true,
			filter: true
		});

		if (c.data.reportType == "budget_plans") {
			colArray.push({
                headerName: 'Budget Amount',
                field: 'budget',
                aggFunc: 'sum',
                type: 'currency',
                enableValue: true
			});
		}

		if (c.data.reportType == "baseline") {
            colArray.push({
                headerName: 'Baseline',
                field: 'baseline',
                aggFunc: 'sum',
                type: 'currency',
                enableValue: true
			});
			colArray.push({
                headerName: 'Execution',
                field: 'execution',
                aggFunc: 'sum',
                type: 'currency',
                enableValue: true
			});
			colArray.push({
                headerName: 'Transfer',
                field: 'transfer',
                aggFunc: 'sum',
                type: 'currency',
                enableValue: true
			});
			colArray.push({
                headerName: 'In Progress',
                field: 'in_progress',
                aggFunc: 'sum',
                type: 'currency',
                enableValue: true
			});
			colArray.push({
                headerName: 'Initiated',
                field: 'initiated',
                aggFunc: 'sum',
                type: 'currency',
                enableValue: true
			});
			colArray.push({
                headerName: 'Committed',
                field: 'committed',
                aggFunc: 'sum',
                type: 'currency',
                enableValue: true
			});
			colArray.push({
                headerName: 'Obligated',
                field: 'obligated',
                aggFunc: 'sum',
                type: 'currency',
                enableValue: true
			});
		}

        if (c.data.reportType == "allocation") {
            colArray.push({
                headerName: 'Allocations',
                field: 'allocations',
                aggFunc: 'sum',
                type: 'currency',
                enableValue: true
            });
            colArray.push({
                headerName: 'Reimbursements',
                field: 'reimbursements',
                aggFunc: 'sum',
                type: 'currency',
                enableValue: true
            });
            colArray.push({
                headerName: 'Transfer In',
                field: 'trans_in',
                aggFunc: 'sum',
                type: 'currency',
                enableValue: true
            });
            colArray.push({
                headerName: 'Transfer Out',
                field: 'trans_out',
                aggFunc: 'sum',
                type: 'currency',
                enableValue: true
            });
            colArray.push({
                headerName: 'VG Labor',
                field: 'vg_labor',
                aggFunc: 'sum',
                type: 'currency',
                enableValue: true
            });
            colArray.push({
                headerName: 'Off. Adjustments',
                field: 'off_adjustments',
                aggFunc: 'sum',
                type: 'currency',
                enableValue: true
            });
            colArray.push({
                headerName: 'Expended',
                field: 'expended',
                aggFunc: 'sum',
                type: 'currency',
                enableValue: true
            });
		}

		colArray.push({
			headerName: 'Balance',
			field: 'balance',
			aggFunc: 'sum',
			type: 'currency',
			enableValue: true
		});

        return colArray;
    }

		var gridOptions = {
			columnDefs: generateColumns(),
			defaultColDef: {
				flex: 1,
				minWidth: 150,
				sortable: true,
				resizable: true
			},
			autoGroupColumnDef: {
				width: 300,
				cellRendererParams: {
					suppressCount: true
				}
			},
			suppressAggFuncInHeader: true,
			groupMultiAutoColumn: true,
			multiSortKey: 'ctrl',
			accentedSort: true,
			pivotMode: true,
			groupIncludeFooter: true,
			groupIncludeTotalFooter: true,
			sideBar: {
				toolPanels: [
				  {
					id: 'columns',
					labelDefault: 'Columns',
					labelKey: 'columns',
					iconKey: 'columns',
					toolPanel: 'agColumnsToolPanel',
				  },
				  {
					id: 'filters',
					labelDefault: 'Filters',
					labelKey: 'filters',
					iconKey: 'filter',
					toolPanel: 'agFiltersToolPanel',
				  },
				],
				position: 'right',
				defaultToolPanel: 'columns',
			},
			columnTypes: {
				'currency': {
					valueFormatter: currencyFormatter,
					cellStyle: currencyCssFunc,
					cellRenderer: 'agAnimateShowChangeCellRenderer'
				}
			}
		};


		c.fiscalYears = [];

		for (var i = 2026; i >= 2000; i--) {
			c.fiscalYears.push(i.toString());
		}

		c.selectedFY = getCurrentFinancialYear();

		c.exportToCsv = function () {
			gridOptions.api.exportDataAsCsv({
				columnGroups: true,
				//customFooter: getGridTotals() NOTE WILL WORK WITH NEW AG GRID LICENSE
			});
		}

		function getGridTotals() {
			var hasColumns = false;
			var totalCounts = {};
			gridOptions.api.forEachNode(function (rowNode, index) {
				if (rowNode.group && rowNode.level == 0) {
					if (!hasColumns) {
						for (var prop in rowNode.aggData) {
							if (Object.prototype.hasOwnProperty.call(rowNode.aggData, prop)) {
								totalCounts[prop] = 0;
							}
						}
						hasColumns = true;
					}
					for (var prop in rowNode.aggData) {
						if (Object.prototype.hasOwnProperty.call(rowNode.aggData, prop)) {
							totalCounts[prop] += rowNode.aggData[prop];
						}
					}
				}
			});

			var totalCols = [];
			var mergeCount = 0;
			var allCols = gridOptions.columnApi.getAllDisplayedColumns();
			allCols.forEach(function (col) {
				if (col.aggFunc != undefined) {
					totalCols.push({
						data: {
							type: 'Number',
							value: totalCounts[col.colId]
						}
					});
				} else {
					mergeCount++;
				}
			});

			return [
				[],
				[{
					data: {
						type: 'String',
						value: 'Grand Total'
					},
					mergeAcross: mergeCount
				}, totalCols]
			];

		};

		function getCurrentFinancialYear() {
			var fiscalyear = "";
			var today = new Date();
			if ((today.getMonth() + 1) >= 10) {
				fiscalyear = (today.getFullYear() + 1);
			} else {
				fiscalyear = today.getFullYear();
			}
			return fiscalyear.toString();
		}

		var gridDiv = document.querySelector('#reportGrid');
		new agGrid.Grid(gridDiv, gridOptions);

		c.generateReport();

		$rootScope.$on('OrgChanged', function (event, args) {
			c.generateReport();
		});
	}

