sys_dictionary.calculation:
(function calculatedFieldValue(current) {
   var priority = 0;
	var gdt = new GlideDateTime();

	var today = gdt.getDate();
    var risk = current.has_risk ? 0 : 1;
	var gdEnd = new GlideDateTime(current.perform_end_date);

	var dur = new GlideDuration();
	dur = GlideDateTime.subtract(today, gdEnd);
	var dayDiff = dur.getDayPart();
	var diff = Math.round(dayDiff / 365);

    if (today < gdEnd && diff == 0) {
        priority = (1 + risk);
    } else if (today < gdEnd && diff == 1) {
        priority = (2 + risk);
    } else if (today < gdEnd && diff == 2) {
        priority = (3 + risk);
    } else if (today > gdEnd) {
        priority = -1;
    } else {
        priority = -999;
	}

	return priority;

	/* var today = gs.getDisplayValue();
    var risk = current.has_risk ? 0 : 1;
    var start = new GlideDate(current.perform_start_date);
    var end = new GlideDate(current.perform_end_date);
    var diff = GlideDate.subtract(gdStart, gdEnd).getYearPart();
    if (today < end && diff == 0) {
        return 1 + risk;
    } else if (today < end && diff == 1) {
        return 2 + risk;
    } else if (today < end && diff == 2) {
        return 3 + risk;
    } else if (today > end) {
        return -1;
    } else {
        return -999;
    }*/
})(current);
