sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
	if(g_form.getValue('operational_status')=='Active')
	{

    var currentDateObj = new Date();
    var currentDateStr = formatDate(currentDateObj, g_user_date_format);
    var currentDateNum = getDateFromFormat(currentDateStr, g_user_date_format);
    var startDateNum = getDateFromFormat(newValue, g_user_date_format);
    if (startDateNum <= currentDateNum) {

		g_form.clearValue('perform_end_date');
		g_form.showErrorBox( 'perform_end_date','Please ensure that the Expiry date is in the future!');
		//g_form.show


        return false;


    }
	}


}
