function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading) {

        if (!g_form.isNewRecord()) {
			if (g_form.getValue('contract_ref') != '') {
            var a = g_form.getReference('contract_ref', boomerang);
            var mod = new GlideAjax('x_g_irm_fms.FMSUtilities');
            mod.addParam('sysparm_name', 'getContractData');
            mod.addParam('sysparm_contract', g_form.getValue('contract_ref'));
            mod.getXML(processYears);
			}


        }


        return;
    }

    if (!g_form.isNewRecord()) {
        var b = g_form.getReference('contract_ref', boomerang);
        var mod1 = new GlideAjax('x_g_irm_fms.FMSUtilities');
        mod1.addParam('sysparm_name', 'getContractData');
        mod1.addParam('sysparm_contract', g_form.getValue('contract_ref'));
        mod1.getXML(processYears);
    }

    function processYears(response) {
        try {
            var answer = response.responseXML.documentElement.getAttribute("answer");

            var resp = answer.split(',');
            g_form.setValue('po_contract_number', resp[0]);
            g_form.setValue('po_award_vendor_name', resp[1]);
        } catch (e) {}

    }

    function boomerang(a) {
        var contract = g_form.setValue('po_award_number', a.contract_piid);
        //  var award = g_form.setValue('po_award_vendor_name', a.vendor_names);
        //alert("a "+contract +" "+award);

    }


    // alert("a "+contract +" "+a.parent_piid.getDisplayValue());

}
