sys_script_client.script:
function onSubmit() {
    //Type appropriate comment here, and begin script below
    if (g_form.getValue('tran_status').toUpperCase() == "COMMITTED") {
        if (g_form.getValue('obligation').toString().length == 0) {
            g_form.showErrorBox('obligation', "Obligation Number is required when status is 'Committed'");
            return false;

        }
    }
	if (g_form.getValue('tran_status').toUpperCase() == "OBLIGATED") {
        if (g_form.getValue('obligation_date')=='') {
            g_form.showErrorBox('obligation_date', "Obligation Date is required when status is 'Obligated'");
            return false;

        }
    }
	if (g_form.getValue('tran_status').toUpperCase() == "OBLIGATED") {
        if (g_form.getValue('allocation_group_ref')=='') {
            g_form.showErrorBox('allocation_group_ref', "Allocation Group is required when status is 'Obligated'");
            return false;

        }
    }

}
