sys_transform_entry.source_script:
answer = (function transformEntry(source) {

	var itemID = "";

	var eCode = source.u_expense_item_id.toString();
	var eCodeLength = eCode.length;
	var ePrefix = "EXP00";
	if (eCodeLength == 1)
		ePrefix += "0000";
	if (eCodeLength == 2)
		ePrefix += "000";
	if (eCodeLength == 3)
		ePrefix += "00";
	if (eCodeLength == 4)
		ePrefix += "0";

	var eNum = ePrefix + eCode;

	var bCode = source.u_budget_id.toString();
	var bCodeLength = bCode.length;
	var bPrefix = "BGT00";
	if (bCodeLength == 1)
		bPrefix += "0000";
	if (bCodeLength == 2)
		bPrefix += "000";
	if (bCodeLength == 3)
		bPrefix += "00";
	if (bCodeLength == 4)
		bPrefix += "0";

	var bNum = bPrefix + bCode;

	var itemQ = new GlideRecord("x_g_irm_fms_budget_item");
	itemQ.addQuery("budget_ref.number", bNum);
	itemQ.addQuery("expense_item_ref.number", eNum);
	itemQ.query();

	if (itemQ.next()) 
		itemID = itemQ.getValue("sys_id");

	// Add your code here
	return itemID; // return the value to be put into the target field

})(source);
